import React from "react";

const Transaction = ({ transaction }) => {
  const sign = transaction.amount < 0 ? "-" : "+";

  return (
    <li className="minus">
      {transaction.text}
      <span>
        {sign}${transaction.amount}
      </span>
      <button className="delete-button">X</button>
    </li>
  );
};

export default Transaction;
